const { v4: uuidv4 } = require('uuid');
const fs = require('fs');
const path = require('path');
const { error } = require('console');
const { User, File } = require('../models');
// Adjust based on your models' structure
const { FOLDER_PATH = '/tmp/files_manager' } = process.env;

// Ensure the storage folder exists
if (!fs.existsSync(FOLDER_PATH)) {
  fs.mkdirSync(FOLDER_PATH, { recursive: true });
}

class FilesController {
  static async postUpload(req, res) {
    const { userId } = req;
    const {
      name, type, parentId = 0, isPublic = false, data,
    } = req.body;
    // validate inputs
    if (!name) {
      return res.status(400).json({ error: 'Missing name' });
    }
    if (!type || !['folder', 'file', 'image'].includes(type)) {
      return res.status(400).json({ error: 'Missing type' });
    }
    if (type !== 'folder' && !data) {
      return res.status(400).json({ error: 'Missing data' });
    }
    // validate parentId if provided
    if (parentId) {
      const parentFile = await File.findById(parentId);
      if (!parentFile) {
        return res.status(400).json({ error: 'Parent not found' });
      }
      if (parentFile.type !== 'folder') {
        return res.status(400).json({ error: 'Parent is not a folder' });
      }
    }
    // creating a new file
    const newFile = {
      userId,
      name,
      type,
      isPublic,
      parentId,
    };

    // handle file creation for type folder
    if (type === 'folder') {
      const createdFolder = await File.create(newFile);
      return res.status(201).json(createdFolder);
    }

    // Handle file creattion for type 'file' or 'image'
    const localFilename = uuidv4();
    const localFilePath = path.join(FOLDER_PATH, localFilename);

    // write the file to disk
    fs.writeFileSync(localFilePath, Buffer.from(data, 'base64'));

    // add localpath to the newFile object
    newFile.localPath = localFilePath;

    // save file to the database
    const createdFile = await File.create(newFile);
    return res.status(201).json(createdFile);
  }
}
module.exports = FilesController;
